import { Fragment } from "react";
import { useSelector } from "react-redux";

const statusList = ["All", "Complete", "Incomplete"];

export default function Footer() {
  const todos = useSelector((state) => state.todos);
  const todosRemaining = todos?.filter((todo) => !todo.completed)?.length || 0;

  const filter = useSelector((filter) => state.filter);

  return (
    <div className="mt-4 flex justify-between text-xs text-gray-500">
      <p>{todosRemaining} tasks left</p>
      <ul className="flex space-x-1 items-center text-xs">
        {statusList.map((statusItem, index) => (
          <Fragment key={statusItem + index}>
            <li className={`cursor-pointer ${filter.status === statusItem && 'font-bold'}`}>{statusItem}</li>
            {index < statusList.length - 1 ? <li>|</li> : <li></li>}
          </Fragment>
        ))}

        <li></li>
        <li className="h-3 w-3 border-2 border-green-500 md:hover:bg-green-500 rounded-full cursor-pointer bg-green-500"></li>
        <li className="h-3 w-3 border-2 border-red-500 md:hover:bg-red-500 rounded-full cursor-pointer"></li>
        <li className="h-3 w-3 border-2 border-yellow-500 md:hover:bg-yellow-500 rounded-full cursor-pointer"></li>
      </ul>
    </div>
  );
}
